import numpy


def calculate_euclidean_distance(a, b):
    """
    The purpose of this definition is to calculate the euclidean distance from two points.
    Please refer to Provost and Fawcett 146
    :param a:
    :param b:
    :return:
    """
    a_2 = numpy.array(a)
    b_2 = numpy.array(b)
    return numpy.linalg.norm(a_2-b_2)

